import React, { useState } from 'react';
import RentalCard from '../components/RentalCard';
import BookingForm from '../components/BookingForm';

const Rentals = () => {
  const [showBookingForm, setShowBookingForm] = useState(false);

  const rentalOptions = [
    {
      title: "Mountain Bike",
      description: "Perfect for trail riding and exploring the Ozarks",
      price: "45",
      features: [
        "Front suspension",
        "All-terrain tires",
        "Multiple frame sizes",
        "Helmet included"
      ]
    },
    {
      title: "Hybrid Bike",
      description: "Comfortable and versatile for both trail and street riding",
      price: "35",
      features: [
        "Upright riding position",
        "Smooth-rolling tires",
        "Easy-to-use gears",
        "Helmet included"
      ]
    },
    {
      title: "Electric Bike",
      description: "Effortless riding with power assist when you need it",
      price: "75",
      features: [
        "Pedal assist up to 20mph",
        "Long-range battery",
        "Multiple power modes",
        "Helmet included"
      ]
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="relative h-64 bg-gray-800">
        <img 
          src="/api/placeholder/1200/400"
          alt="Nature background"
          className="w-full h-full object-cover opacity-70"
        />
        <div className="absolute inset-0 flex items-center justify-center">
          <h1 className="text-4xl font-bold text-white text-center">
            Bike Rentals
          </h1>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {!showBookingForm ? (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
              {rentalOptions.map((option, index) => (
                <RentalCard
                  key={index}
                  {...option}
                />
              ))}
            </div>
            <div className="text-center">
              <button
                onClick={() => setShowBookingForm(true)}
                className="bg-brand-orange text-white px-8 py-3 rounded-lg text-lg font-semibold hover:bg-orange-600 transition duration-300"
              >
                Make a Reservation
              </button>
            </div>
          </>
        ) : (
          <div className="max-w-2xl mx-auto">
            <h2 className="text-3xl font-bold text-brand-black mb-8 text-center">
              Book Your Rental
            </h2>
            <BookingForm />
            <button
              onClick={() => setShowBookingForm(false)}
              className="mt-4 text-brand-orange hover:text-orange-600 font-medium"
            >
              ‚Üê Back to rental options
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Rentals;
